import React from "react";
import { useState} from "react";

import styled from "./MovieForm.module.css";

import { addMovie, updateMovie } from "../../service/MovieService";

import { useNavigate , useLocation } from 'react-router-dom';
import { useEffect } from "react";

const MovieForm = () => {


    const navigate = useNavigate();

    const location = useLocation();
    const{state} = location;


    const [movie, setMovie] = useState({name:"",director:"",laguage:"",genre:""})
const [ formValues,setFormValues]=useState();
const [ formErrors,setFormErrors]=useState({});
const [isSubbmit,serIsSubmit]=useState(false);
    useEffect(()=>{
        if(state.action==="update"){
            setMovie(state.movie)
        }
    },[state.action,state.movie])

    const onChangeHandler = (e) =>{
        setMovie({...movie, [e.target.name]: e.target.value});
        setFormValues({...movie, [e.target.name]: e.target.value});
    }

    const submitHandler=(e)=>{

        e.preventDefault();
        setFormErrors(validate(formValues));
        serIsSubmit(true);
        useEffect(()=>{
          if(Object.keys(formErrors).length===0&&isSubbmit){
            console.log(formValues);
          }
        })
        if(state.action==="add"){
        addMovie(movie)
        .then(reponse=>{
            console.log(reponse);
            navigate("/library")
        })
        .catch(err=>{
            console.log(err);
        })
        }

        if(state.action==="update"){
            updateMovie(movie, state.movie.id)
            .then(reponse=>{
                console.log(reponse);
                navigate("/library")
            })
            .catch(err=>{
                console.log(err);
            })
            }

    }
const validate=(values)=>{
const errors={};

if(!values.name){
  errors.name="Required name"
}
if(!values.director){
  errors.director="Required director"
}
if(!values.language){
  errors.language="Required language"
}
if(!values.genre){
  errors.genre="Required genre"
}
}
  return (
    <React.Fragment>
        <div className={styled.movieForm}>
            <div className=" btn-dark mt-2 pt-2 pb-2">{state.action==="update"?"Update Movie": "Add Movie"}</div>
      <form className="mb-2" >

        <div className="form-group">
          <label for="name">Enter Name</label>
          <input
            type="text"
            class="form-control"
            id="name"
            placeholder="Enter Name"
            name="name"
            onChange={onChangeHandler}
            value={movie.name&&formValues.name}
          />
          <p>{formErrors.name}</p>
        </div>
        <div class="form-group">
          <label for="director">Enter Director</label>
          <input
            type="text"
            class="form-control"
            id="director"
            placeholder="Enter Director"
            name="director"
            onChange={onChangeHandler}
            value={movie.director&&formValues.director}
          />
           <p>{formErrors.director}</p>
        </div>
        <div class="form-group">
          <label for="language">Enter Language</label>
          <input
            type="text"
            class="form-control"
            id="language"
            placeholder="Enter Language"
            name="language"
            onChange={onChangeHandler}
            value={movie.language&&formValues.laguage}
          />
           <p>{formErrors.laguage}</p>
        </div>
        <div class="form-group">
          <label for="genre">Enter Genre</label>

          <input
            type="text"
            class="form-control"
            id="genre"
            placeholder="Enter Genre"
            name="genre"
            onChange={onChangeHandler}
            value={movie.genre&&formValues.genre}
          />
           <p>{formErrors.genre}</p>
        </div>
        <button type="submit" class="btn btn-primary" onClick={submitHandler}>
          {state.action==="update"?"Update Movie":"Add Movie"}
        </button>
      </form>
      </div>
    </React.Fragment>
  );
};

export default MovieForm;
